pragma solidity 0.4.24;

import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC721/ERC721.sol";


library IsClaimOk {
  function ethereum(uint256 amount, uint256 weiSend) internal view returns (bool) {
    if (amount < weiSend) return false;
    // claiming ethereum, amount not enough

    return true;
  }

  function erc20(uint256 amount, address token, address from, address to) internal view returns (bool) {
    uint256 allowedAmount = ERC20(token).allowance(from, to);
    if (allowedAmount < amount) return false;
    // claiming erc20, amount not enough

    return true;
  }

  function erc721(uint256 tokenId, address token, address to) internal view returns (bool) {
    address approved = ERC721(token).getApproved(tokenId);
    if (approved != to) return false;
    // claiming erc721, not approved

    return true;
  }
}
